// HadithQueryResults.js
import React from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;

  const { hadithNumbers, themes, texts } = resultsData;

  return (
    <div className="hadith-query-results">
      <div className="back-button-HQR" onClick={() => window.history.back()}>
        <img src={require('../../assets/back_button.png')} alt="Back Button" />
      </div>
      <div className="query-text-box">Query Text</div>

      {hadithNumbers && themes && texts && hadithNumbers.length > 0 ? (
        <div className="details-table">
          <h2>Table 1</h2>
          <table>
            <tbody>
              <tr>
                <th>Hadith Number</th>
                <th>Text</th>
                <th>Theme</th>
              </tr>
              {text.map((item, index) => (
                <tr key={index}>
                  <td>{item}</td>
                  <td>{texts[index]}</td>
                  <td>{themes[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div>No data available</div>
      )}
    </div>
  );
};

export default HadithQueryResults;
