import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;

  const [isExpanded, setIsExpanded] = useState(false);
  const [sortOrder, setSortOrder] = useState('asc'); // Initial sort order
  const [sortedColumn, setSortedColumn] = useState(null); // Initial sorted column

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  // Function to handle sorting
  const handleSort = (column) => {
    let sortedData = [...resultsData.HadithNo]; // Copy of Hadith Numbers

    if (sortOrder === 'asc') {
      sortedData.sort((a, b) => a - b); // Sort in ascending order
      setSortOrder('desc');
    } else {
      sortedData.sort((a, b) => b - a); // Sort in descending order
      setSortOrder('asc');
    }

    setSortedColumn(column);
    return sortedData;
  };

  return (
    <div className={`hadith-query-results ${isExpanded ? 'expanded' : ''}`}>
      {/* ... Your existing code ... */}

      {isExpanded && resultsData && resultsData.HadithNo && resultsData.Theme && resultsData.Text && (
        <div className="details-table">
          <table>
            <tbody>
              <tr>
                <th className={`sortable ${sortedColumn === 'HadithNo' ? 'sorted' : ''}`} onClick={() => handleSort('HadithNo')}>
                  Hadith Number {sortedColumn === 'HadithNo' && sortOrder === 'asc' ? '▲' : '▼'}
                </th>
                <th className={`sortable ${sortedColumn === 'Theme' ? 'sorted' : ''}`} onClick={() => handleSort('Theme')}>
                  Theme {sortedColumn === 'Theme' && sortOrder === 'asc' ? '▲' : '▼'}
                </th>
                <th className={`sortable ${sortedColumn === 'Text' ? 'sorted' : ''}`} onClick={() => handleSort('Text')}>
                  Text {sortedColumn === 'Text' && sortOrder === 'asc' ? '▲' : '▼'}
                </th>
              </tr>
              {resultsData.HadithNo.map((item, index) => (
                <tr key={index}>
                  <td>{item}</td>
                  <td>{resultsData.Theme[index]}</td>
                  <td className="text-cell">{resultsData.Text[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default HadithQueryResults;
