import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;

  const [isExpanded, setIsExpanded] = useState(false);
  const [sortOrder, setSortOrder] = useState('asc');
  const [sortedResultsData, setSortedResultsData] = useState({
    HadithNo: [...resultsData.HadithNo],
    Theme: [...resultsData.Theme],
    Text: [...resultsData.Text]
  });

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const handleSort = () => {
    const sortedHadithNo = [...sortedResultsData.HadithNo];
    const sortedTheme = [...sortedResultsData.Theme];
    const sortedText = [...sortedResultsData.Text];

    if (sortOrder === 'asc') {
      sortedHadithNo.sort((a, b) => a - b);
      setSortOrder('desc');
    } else {
      sortedHadithNo.sort((a, b) => b - a);
      setSortOrder('asc');
    }

    setSortedResultsData({
      HadithNo: sortedHadithNo,
      Theme: sortedHadithNo.map(hadithNo => sortedTheme[sortedResultsData.HadithNo.indexOf(hadithNo)]),
      Text: sortedHadithNo.map(hadithNo => sortedText[sortedResultsData.HadithNo.indexOf(hadithNo)])
    });
  };

  return (
    <div className="hadith-query-results">
      <div className="back-button-HQR" onClick={() => window.history.back()}>
        <img src={require('../../assets/back_button.png')} alt="Back Button" />
      </div>
      <div className={`query-text-box ${isExpanded ? 'expanded' : ''}`}>
        <div className="expand-icon" onClick={toggleExpand}>
          <img src={require('../../assets/expand_icon.png')} alt="Expand Icon" />
        </div>

        <div className="details-swipe-bar" onClick={toggleExpand}>
          <div className={`arrow ${isExpanded ? 'expanded' : ''}`}></div>
        </div>
      </div>

      {isExpanded && resultsData && resultsData.HadithNo && resultsData.Theme && resultsData.Text && (
        <div className="details-table">
          <table>
            <tbody>
              <tr>
                <th className="sortable" onClick={handleSort}>
                  Hadith Number {sortOrder === 'asc' ? '▲' : '▼'}
                </th>
                <th>Theme</th>
                <th>Text</th>
              </tr>
              {sortedResultsData.HadithNo.map((item, index) => (
                <tr key={index}>
                  <td>{item}</td>
                  <td>{sortedResultsData.Theme[index]}</td>
                  <td className="text-cell">{sortedResultsData.Text[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default HadithQueryResults;
