import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;

  const [isExpanded, setIsExpanded] = useState(false);
  const [sorting, setSorting] = useState({
    column: 'Hadith Number',
    direction: 'asc', // 'asc' for ascending, 'desc' for descending
  });
  const [sortedData, setSortedData] = useState([]);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  useEffect(() => {
    // Create a copy of the data for sorting
    const dataCopy = [...resultsData.HadithNo];

    if (sorting.column === 'Hadith Number') {
      // Sort the data based on Hadith Number
      dataCopy.sort((a, b) => {
        if (sorting.direction === 'asc') {
          return a - b;
        } else {
          return b - a;
        }
      });
    } else {
      // If any other column is selected, keep the order as is
      // You can add sorting logic for other columns here if needed
    }

    setSortedData(dataCopy);
  }, [sorting, resultsData.HadithNo]);

  const handleSort = (column) => {
    setSorting((prevSorting) => {
      if (prevSorting.column === column) {
        return {
          column,
          direction: prevSorting.direction === 'asc' ? 'desc' : 'asc',
        };
      } else {
        return {
          column,
          direction: 'asc',
        };
      }
    });
  };

  return (
    <div className={`hadith-query-results ${isExpanded ? 'expanded' : ''}`}>
      <div className="back-button-HQR" onClick={() => window.history.back()}>
        <img src={require('../../assets/back_button.png')} alt="Back Button" />
      </div>
      <div className={`query-text-box ${isExpanded ? 'expanded' : ''}`}>
        <div className="details-swipe-bar" onClick={toggleExpand}>
          <div className={`arrow ${isExpanded ? 'expanded' : ''}`}></div>
        </div>
      </div>

      {isExpanded && resultsData && resultsData.HadithNo && resultsData.Theme && resultsData.Text && (
        <div className="details-table">
          <table>
            <tbody>
              <tr>
                <th className="sortable" onClick={() => handleSort('Hadith Number')}>
                  Hadith Number
                  {sorting.column === 'Hadith Number' && (
                    <span className={`arrow ${sorting.direction === 'asc' ? 'asc' : 'desc'}`}></span>
                  )}
                </th>
                <th>Theme</th>
                <th>Text</th>
              </tr>
              {sortedData.map((item, index) => (
                <tr key={index}>
                  <td>{resultsData.HadithNo[item]}</td>
                  <td>{resultsData.Theme[item]}</td>
                  <td className="text-cell">{resultsData.Text[item]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default HadithQueryResults;
