import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;

  const [isExpanded, setIsExpanded] = useState(false);
  const [sortOrder, setSortOrder] = useState('asc');
  const [sortedResults, setSortedResults] = useState(null);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  useEffect(() => {
    if (resultsData) {
      handleSort();
    }
  }, [resultsData]);

  const handleSort = () => {
    if (resultsData) {
      let sortedData = [...resultsData.HadithNo];

      if (sortOrder === 'asc') {
        sortedData.sort((a, b) => a - b);
        setSortOrder('desc');
      } else {
        sortedData.sort((a, b) => b - a);
        setSortOrder('asc');
      }

      const sortedTheme = sortedData.map(hadithNo => resultsData.Theme[resultsData.HadithNo.indexOf(hadithNo)]);
      const sortedText = sortedData.map(hadithNo => resultsData.Text[resultsData.HadithNo.indexOf(hadithNo)]);

      setSortedResults({ HadithNo: sortedData, Theme: sortedTheme, Text: sortedText });
    }
  };

  return (
    <div className={`hadith-query-results ${isExpanded ? 'expanded' : ''}`}>
      {/* ... rest of your code ... */}
      {isExpanded && sortedResults && sortedResults.HadithNo && sortedResults.Theme && sortedResults.Text && (
        <div className="details-table">
          <table>
            <tbody>
              <tr>
                <th className="sortable" onClick={handleSort}>
                  Hadith Number {sortOrder === 'asc' ? '▲' : '▼'}
                </th>
                <th>Theme</th>
                <th>Text</th>
              </tr>
              {sortedResults.HadithNo.map((item, index) => (
                <tr key={index}>
                  <td>{item}</td>
                  <td>{sortedResults.Theme[index]}</td>
                  <td className="text-cell">{sortedResults.Text[index]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default HadithQueryResults;
