import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;

  const [isExpanded, setIsExpanded] = useState(false);
  const [sortOrder, setSortOrder] = useState('asc');
  const [sortedData, setSortedData] = useState(resultsData); // Store a copy of data for sorting

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const handleSort = () => {
    const sorted = { ...sortedData };

    if (sortOrder === 'asc') {
      sorted.HadithNo.sort((a, b) => a - b);
      setSortOrder('desc');
    } else {
      sorted.HadithNo.sort((a, b) => b - a);
      setSortOrder('asc');
    }

    // Sort the other arrays based on the sorted HadithNo
    sorted.Theme = sorted.Theme.map((_, index) => sortedData.Theme[sortedData.HadithNo.indexOf(sorted.HadithNo[index])]);
    sorted.Text = sorted.Text.map((_, index) => sortedData.Text[sortedData.HadithNo.indexOf(sorted.HadithNo[index])]);

    setSortedData(sorted);
  };

  return (
    <div className="hadith-query-results">
      {/* ... Your other JSX ... */}
      <div className="details-table">
        <table>
          <tbody>
            <tr>
              <th className="sortable" onClick={handleSort}>
                Hadith Number {sortOrder === 'asc' ? '▲' : '▼'}
              </th>
              <th>Theme</th>
              <th>Text</th>
            </tr>
            {sortedData.HadithNo.map((item, index) => (
              <tr key={index}>
                <td>{item}</td>
                <td>{sortedData.Theme[index]}</td>
                <td className="text-cell">{sortedData.Text[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default HadithQueryResults;
