import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './HadithQueryResults.css';

const HadithQueryResults = () => {
  const location = useLocation();
  const { resultsData } = location.state;
  const { Text, HadithNo, Theme } = resultsData;

  const [isSwipeBarExpanded, setIsSwipeBarExpanded] = useState(false); // Start with swipe bar collapsed
  const [isExpanded, setIsExpanded] = useState(false); // Start with details collapsed
  const [isAscendingOrder, setIsAscendingOrder] = useState(true); // Initial order is ascending

  const toggleSwipeBarExpand = () => {
    setIsSwipeBarExpanded(!isSwipeBarExpanded);
  };

  const toggleExpand = () => {
    if (isSwipeBarExpanded) {
      setIsExpanded(!isExpanded);
    }
  };

  const toggleSortOrder = () => {
    setIsAscendingOrder(!isAscendingOrder);
  };

  const sortedHadithNo = isAscendingOrder ? [...HadithNo].sort() : HadithNo;

  return (
    <div className="hadith-query-results">
      <div className="back-button-HQR" onClick={() => window.history.back()}>
        <img src={require('../../assets/back_button.png')} alt="Back Button" />
      </div>
      <div className={`query-text-box ${isSwipeBarExpanded ? 'swipe-bar-expanded' : ''}`}>
        <div className="expand-icon" onClick={toggleExpand}>
          <img src={require('../../assets/expand_icon.png')} alt="Expand Icon" />
        </div>
        Query Text
        <div
          className={`details-swipe-bar ${isSwipeBarExpanded ? 'expanded' : ''}`}
          onClick={toggleSwipeBarExpand}
        >
          <div className={`arrow ${isSwipeBarExpanded ? 'expanded' : ''}`}></div>
        </div>
      </div>

      <div className="details-table">
        <table>
          <thead>
            <tr>
              <th onClick={toggleSortOrder}>Hadith Number</th> {/* Make the heading clickable */}
              <th>Theme</th>
              <th>Text</th>
            </tr>
          </thead>
          <tbody>
            {sortedHadithNo.map((item, index) => (
              <tr key={index}>
                <td>{item}</td>
                <td>{Theme[index]}</td>
                <td className="text-cell">{Text[index]}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default HadithQueryResults;
